import {SearchContact} from "./index";

import {useLocation} from 'react-router-dom';

import {BACKGROUND, PURPLE} from "../helpers/colors";


const Navbar = ({}) => {
    const location = useLocation();
    return (
        <nav className="navbar navbar-dark navbar-expand-sm shadow-lg" style={{backgroundColor: BACKGROUND}}>
            <div className="container">
                <div className="row w-100 my-3">
                    <div className="col">
                        <div className="navbar-brand">
                            <i className="fas fa-id-badge py-2" style={{color: PURPLE, fontSize: 25}}/>{" "}
                            <span>وب اپلیکیشن مدیریت {" "}</span>
                            <span className="" style={{color: PURPLE}}>مخاطبین</span>
                        </div>
                    </div>
                    {
                        location.pathname === "/contacts" ? (
                            <div className="col">
                                <SearchContact />
                            </div>
                        ) :null
                    }
                </div>
            </div>
        </nav>
    )
}

export default Navbar;
